import React, { useState } from 'react';
import './SideNav.css';

interface NavItem {
  id: string;
  label: string;
  icon: string;
  hasChevron?: boolean;
  isDropdown?: boolean;
}

interface NavCategory {
  title: string;
  items: NavItem[];
}

const SideNav: React.FC = () => {
  const [isNooksDropdownOpen, setIsNooksDropdownOpen] = useState(false);

  const navigationData: NavCategory[] = [
    {
      title: '',
      items: [
        { id: 'recommendations', label: 'Recommendations', icon: '‚≠ê' },
        { id: 'tasks', label: 'Tasks', icon: '‚òëÔ∏è' }
      ]
    },
    {
      title: 'Call',
      items: [
        { id: 'salesfloor', label: 'Salesfloor', icon: 'üìπ' },
        { id: 'dial', label: 'Dial', icon: 'üìû' },
        { id: 'call-library', label: 'Call library', icon: '‚ñ∂Ô∏è' },
        { id: 'coach', label: 'Coach', icon: 'üí≠' }
      ]
    },
    {
      title: 'Engage',
      items: [
        { id: 'sequences', label: 'Sequences', icon: '‚úàÔ∏è' },
        { id: 'email-outbox', label: 'Email outbox', icon: '‚úâÔ∏è' },
        { id: 'templates', label: 'Templates', icon: 'üìÑ' },
        { id: 'activity', label: 'Activity', icon: 'üîÑ' }
      ]
    },
    {
      title: 'Prospect',
      items: [
        { id: 'accounts', label: 'Accounts', icon: 'üè¢' },
        { id: 'people', label: 'People', icon: 'üë§' },
        { id: 'enrichment', label: 'Enrichment', icon: 'üîÑ' }
      ]
    },
    {
      title: 'Analytics',
      items: [
        { id: 'reporting', label: 'Reporting', icon: 'üìä', hasChevron: true }
      ]
    },
    {
      title: 'Automation',
      items: [
        { id: 'automations', label: 'Automations', icon: '‚öôÔ∏è' },
        { id: 'plays', label: 'Plays', icon: 'üßä', hasChevron: true }
      ]
    }
  ];

  const handleNavItemClick = (itemId: string) => {
    console.log(`Navigated to: ${itemId}`);
    // Add your navigation logic here
  };

  return (
    <nav className="side-nav">
      {/* Top border */}
      <div className="nav-border top"></div>

      {/* Header */}
      <div className="nav-header">
        <div 
          className="nooks-brand"
          onClick={() => setIsNooksDropdownOpen(!isNooksDropdownOpen)}
        >
          <div className="nooks-icon">N</div>
          <span className="nooks-text">Nooks</span>
          <span className={`chevron ${isNooksDropdownOpen ? 'open' : ''}`}>‚ñº</span>
        </div>
      </div>

      {/* Navigation Items */}
      <div className="nav-items">
        {navigationData.map((category, categoryIndex) => (
          <div key={categoryIndex} className="nav-category">
            {category.title && (
              <h3 className="category-title">{category.title}</h3>
            )}
            {category.items.map((item) => (
              <div
                key={item.id}
                className="nav-item"
                onClick={() => handleNavItemClick(item.id)}
              >
                <span className="nav-icon">{item.icon}</span>
                <span className="nav-label">{item.label}</span>
                {item.hasChevron && <span className="item-chevron">‚Ä∫</span>}
              </div>
            ))}
          </div>
        ))}
      </div>

      {/* Settings at bottom */}
      <div className="nav-settings">
        <div className="nav-item">
          <span className="nav-icon">‚öôÔ∏è</span>
          <span className="nav-label">Settings</span>
        </div>
      </div>

      {/* Bottom border */}
      <div className="nav-border bottom"></div>
    </nav>
  );
};

export default SideNav;
